{% if doc.medicines|length <= 8 %}
    <!DOCTYPE html>
    <html lang="en">
    <head>
        <style>
            @page {
                size: A4 portrait;
                margin: 3mm;
            }

            .page-container {
                font-family: Arial, sans-serif;
                width: 210mm;
                height: 148.5mm;
                page-break-after: always;
                border: 1px solid #000;
                padding: 1mm;
                overflow: hidden;
                position: relative;
            }

            .invoice-table {
                width: 100%;
                border-collapse: collapse;
                margin: -1px 0 2px 0;
            } 

            .invoice-table td {
                border: 1px solid black;
                padding: 1px;
                margin-top: 0px;
                font-size: 11px; /* Minimum font size */
            }

            .content-wrapper {
                display: flex;
                flex-direction: column;
                height: calc(100% - 25px);
            }

            .order-details {
                width: 100%;
                border-collapse: collapse;
                font-size: 11px; /* Minimum font size */
                flex: 0 0 auto;
            }

            .order-details th, .order-details td {
                padding: 0px; /* Reduce padding to fit more rows */
                font-size: 11px; /* Minimum font size */
                line-height: 0.8; /* Reduce extra spacing */
            }

            .order-details tr.header-row th {
                border: 1px solid black;
                padding: 0px;
                text-align: center;
                font-size: 11px;
            }

            .order-details tr.product-row td {
                border-left: 1px solid black;
                border-right: 1px solid black;
                border-top: none;
                border-bottom: none;
                padding: 0px;
                font-size: 11px;
                line-height: 0.6;
            }

            .order-details tr.total-row th {
                border: 1px solid black;
                padding: 0.5px;
                font-size: 11px;
            }

            .footer-container {
                font-size: 11px;
                margin-top: 2px;
                flex: 0 0 auto;
                width: 100%;
            }

            .footer-container p {
                margin: 0;
                line-height: 1.1;
            }

            .footer-flex {
                display: flex;
                justify-content: space-between;
                width: 100%;
            }

            .refund-policy {
                margin: 0;
                line-height: 1.2;
            }

            .delivery-contact {
                text-align: center;
                margin-top: 2px;
            }


        </style>
    </head>

    <body>
        <div class="page-container">
            <table class="invoice-table">
                <tr>
                    <td style="text-align:right; width: 25%;">
                        <img src="/assets/healthcare_management/images/final_logo_3.png" alt="Clinic Logo" style="object-fit: contain; scale: 1.5">
                    </td>
                    <td style="text-align: center; font-size: 11px; width: 25%;">
                        <br>
                        <span style="font-weight: bold;">
                                kowdiar,Trivandrum-695003<br>
                                Phone:0471-4612849,8891079555<br>
                                E-drrasheeds@gmail.com
                        </span>
                    </td>

                    <td style="text-align:left; font-size: 12px; width: 50%; font-weight: bold;">
                            {% set patient_doc = frappe.get_doc("Patient", {'patient_name': doc.patient_name}) %}
                            <span>Token No&nbsp;:&nbsp;&nbsp;</span> <br>
                            <span>Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;<span style="font-size:14px;">{{ doc.patient_name }}</span></span><br>
                            <span>Address&nbsp;&nbsp;:&nbsp;&nbsp;{{ patient_doc.custom_address_line }},&nbsp;{{ patient_doc.custom_city }},
                                &nbsp;{{ patient_doc.custom_pincode }},&nbsp;{{ patient_doc.custom_state }},&nbsp;{{ patient_doc.custom_country }}</span>
                            <br>
                            <span>Mobile&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;{{ patient_doc.mobile if patient_doc.mobile else '' }}</span><br>
                            {% set doctor_doc = frappe.get_doc("Healthcare Practitioner", doc.healthcare_practitioner) %}
                            <span>Doctor&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp;&nbsp;<span style="font-size:14px;">{{ doctor_doc.practitioner_name }}</span></span> 
                    </td>
                </tr> 
            </table>

            <div class="content-wrapper">
                <table class="order-details">
                    <tr class="header-row">
                        <th colspan="2" style="width: 50%; font-size: 22px; font-weight: bold; text-align: center;">
                            <br>
                            <span>CLINICAL PROCEDURE INVOICE</span>
                        </th>
                        <th colspan="4" style="width: 50%; text-align: start;">
                            <span style="font-weight: bold;">
                                Invoice No: A000982<br><br>
                                Billed by: {{ frappe.session.user }}<br><br>
                                Date and Time: {{ doc.date_and_time }}
                            </span>
                        </th>
                    </tr>

                    <tr class="header-row">
                        <th style="width: 5%; "><span style="font-weight: bold;">SN</span></th>
                        <th style="width: 45%;"><span style="font-weight: bold;">DESCRIPTION</span></th>
                        <th style="width: 7%;"><span style="font-weight: bold;">QTY</span></th>
                        <th style="width: 15%;"><span style="font-weight: bold;">RATE</span></th>
                        <th style="width: 8%;"><span style="font-weight: bold;">DISC</span></th>
                        <th style="width: 20%;"><span style="font-weight: bold;">AMOUNT</span></th>
                    </tr>

                    {% set max_rows = 8 %}
                    {% set num_items = doc.nursing_items|length %}
                    {% for item in doc.nursing_items[:max_rows] %}
                    <tr class="product-row">
                        <td style="text-align: center;">{{ loop.index }}</td>
                        <td style="text-align: start;">{{ item.item_name }}</td>
                        <td style="text-align: center;">{{ item.qty }}</td>
                        <td style="text-align: end;">₹ {{ '%.2f'| format(item.rate) }}</td>
                        <td style="text-align: end;">{{ '%.2f'| format(item.discount_)}} %</td>
                        <td style="text-align: end;">₹ {{ '%.2f'| format(item.amount) }}</td>
                    </tr>
                    {% endfor %}

                    <!-- Fill empty rows if less than 12 items -->
                    {% for i in range(num_items, max_rows) %}
                    <tr class="product-row">
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                        <td>&nbsp;</td>
                    </tr>
                    {% endfor %}

                    <!-- Product rows remain same -->

                    <tr class="total-row">
                        <th colspan="3">
                            <span style="font-weight: bold; font-size: 12px;">{{ doc.amount_in_words }}</span><br><br>
                            <p style="font-weight: bold; font-size: 12px;"> 
                                TOTAL ITEM : {{ doc.total_items }} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 
                                TOTAL QTY: {{ doc.total_quantity }} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 
                                YOU SAVED RUPEES :₹ {{ "{:,.2f}".format(doc.saved_amount | float) }}
                            </p>
                        </th>
                        <th colspan="3" style="text-align: end;">
                            <span style="font-weight: bold; font-size: 20px;">GRAND TOTAL &nbsp;&nbsp;&nbsp;₹ {{ '%.2f'| format(doc.rounded_total_amount) }}</span>
                        </th>
                    </tr>
                </table>

                <div class="table-border-close"></div>

                <!--<div class="spacer"></div>-->

                <!-- Updated Footer Section -->
                <div class="footer-container">
                    <div class="refund-policy" style="font-size: 9px;">
                        <p>You have to be with someone relative when coming for procedures.</p>
                        <p>Refund policy is not available for Clinical procedure fee.</p>
                    </div>
                    <br>
                    <div class="footer-flex">
                        <span style="font-weight: bold; text-align: start;">
                                GSTIN: 32CHZPS7837K1Z9&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;DL NO: KL-IVM-100946, KL-TVM-100947
                            </span>
                    </div>
                </div>
            </div>
        </div>
    </body>
    </html>
{%endif%}

